ScorecardTable.js
import React, { useState } from 'react';
import '../styles/ScorecardTable.css';

const ScorecardTable = ({ holes }) => {
    const rowTitles = ["Hole", "HCP", "Par", "Adj Par"];

    return (
        <table>
            <thead>
                <tr>
                    <th>{rowTitles[0]}</th>
                    {holes.map(hole => (
                        <th key={hole.hole_number}>{hole.hole_number}</th>
                    ))}
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>{rowTitles[1]}</td>
                    {holes.map(hole => (
                        <td key={`hcp-${hole.hole_number}`}>{hole.hcp}</td>
                    ))}
                </tr>
                <tr>
                    <td>{rowTitles[2]}</td>
                    {holes.map(hole => (
                        <td key={`par-${hole.hole_number}`}>{hole.par}</td>
                    ))}
                </tr>
                <tr>
                    <td>{rowTitles[3]}</td>
                    {holes.map(hole => (
                        <td key={`adjpar-${hole.hole_number}`}></td>
                    ))}
                </tr>
            </tbody>
        </table>
    );
};

export default ScorecardTable;
