import React, { useState } from 'react';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import ScorecardTable from './components/ScorecardTable';
import Options from './components/Options';
import './styles/App.css';

const App = () => {
    const [holes, setHoles] = useState([]);

    const handleSearch = (courseName) => {
        fetch(`http://localhost:3001/api/golf-course?name=${courseName}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setHoles(data.holes))
            .catch(error => console.error('Error fetching data:', error));
    };

    const handlePar99 = () => {
        const newHoles = holes.map(hole => {
            if (hole.hole_number >= 10 && hole.hole_number <= 18) {
                return { ...hole, hcp: hole.hcp + 1 };
            } else if (hole.hole_number >= 1 && hole.hole_number <= 9) {
                return { ...hole, hcp: hole.hcp + 2 };
            }
            return hole;
        });
        setHoles(newHoles);
    };

    return (
        <div className="App">
            <Header />
            <SearchBar onSearch={handleSearch} />
            <ScorecardTable holes={holes} />
            <Options onPar99={handlePar99} />
        </div>
    );
};

export default App;
