import React from 'react';
import '../styles/ScorecardTable.css';

const ScorecardTable = ({ holes = [] }) => {
    const rowTitles = ["Hole", "HCP", "Par", "Adj Par"];

    // Generate an array of hole numbers from 1 to 18
    const holeNumbers = Array.from({ length: 18 }, (_, i) => i + 1);

    return (
        <table>
            <thead>
                <tr>
                    <th>{rowTitles[0]}</th>
                    {holeNumbers.map(holeNumber => (
                        <th key={holeNumber}>{holeNumber}</th>
                    ))}
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>{rowTitles[1]}</td>
                    {holes.map(hole => (
                        <td key={`hcp-${hole.hole_number}`}>{hole.hcp}</td>
                    ))}
                </tr>
                <tr>
                    <td>{rowTitles[2]}</td>
                    {holes.map(hole => (
                        <td key={`par-${hole.hole_number}`}>{hole.par}</td>
                    ))}
                </tr>
                <tr>
                    <td>{rowTitles[3]}</td>
                    {holes.map(hole => {
                        const adjPar = hole.hcp >= 10 ? hole.par + 1 : hole.par + 2;
                        return <td key={`adjpar-${hole.hole_number}`}>{adjPar}</td>;
                    })}
                </tr>
            </tbody>
        </table>
    );
};

export default ScorecardTable;
